# Функция парсинга и вывода основных параметров НПА из текста
def handle_fields(text: str):
    name = re.search(r'(?<=РОССИЙСКАЯ\sФЕДЕРАЦИЯ\sФЕДЕРАЛЬНЫЙ\sЗАКОН).+?(?=Принят)|ПЕРЕЧЕНЬ\s[^I]{1,250}(?=утвержденное)|(Положение\s+(о|об)|ПОЛОЖЕНИЕ).+?(?=,\s+утвержденное|I|\d\.|в ред|\(|Список)|ИНСТРУКЦИЯ\s[^I]{1,250}', text)
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Наименование: {bcolors.ENDC}{bcolors.WARN}", name[0].replace(".", "") if name else 'Отсутствует')
    number = re.search(r'(N|№)\s\d{1,4}\w{0,3}', text)
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Подписной номер: {bcolors.ENDC}{bcolors.WARN}", number[0] if number else 'Отсутствует')
    date = re.search(r'\d{1,2}\s\w{3,8}\s\d{4}\sг.|\d{1,2}\.\d{1,2}\.\d{4}', text)
    datet = datetime.now()
    if date:
        if len(date[0]) > 10:
            datet = todate(date[0])
        else:
            datet = datetime.strptime(date[0], '%d.%m.%Y')
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Дата вступления в силу: {bcolors.ENDC}{bcolors.WARN}", datet if date else 'Отсутствует')
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Актуальность: {bcolors.ENDC}{bcolors.WARN}", "Проект" if date and datet > datetime.now() else 'Действует')
    level = re.search(r'(?i)((?<=приказ)|(?<=Указ)|(?<=Постановление))\s.+?(РФ|Федерации)(?=\s)|(?<=-)ФЗ|начальника.+?(РФ|Федерации).+?(РФ|Федерации)', text)
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Уровень: {bcolors.ENDC}{bcolors.WARN}", level[0] if level else 'Отсутствует')
    dtype = re.search(r'(?i)приказ|указ|Постановление|Федеральный закон', text)
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Вид: {bcolors.ENDC}{bcolors.WARN}", dtype[0] if dtype else 'Отсутствует')
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Основной ли это документ: {bcolors.ENDC}{bcolors.WARN}", 'Да')
    print(f"   {bcolors.ENDC}\u221f {bcolors.BOLD}Для какого документа это дополнение: {bcolors.ENDC}{bcolors.WARN}", 'Отсутствует')
    print(f"   {bcolors.ENDC}__________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________")


    trace = open("полномочия_trace.log", "a")
    trace.write("Дата вступления в силу: " + str(datet if date else 'Отсутствует') + "\n")
    trace.write("Актуальность: " + str("Проект" if date and datet > datetime.now() else 'Действует') + "\n")
    trace.write("Уровень: " + str(level[0] if level else 'Отсутствует') + "\n")
    trace.write("Вид: " + str(dtype[0] if dtype else 'Отсутствует') + "\n")
    trace.write("Основной ли это документ: Да\n")
    trace.write("Для какого документа это дополнение: Отсутствует')\n")
    trace.write("________________________________________________________________________________________________________________________________________________________________________________________________________\n")
    trace.close()

