# Функция перевода даты из текстового формата (который используется в НПА) в формат datetime
def todate(date: datetime):
    day = re.search(r'\d{1,2}\s', date)
    month = re.search(r'\w{3,8}', date)
    year = re.search(r'\d{4}', date)
    month_digit = ''
    if month[0] == 'января':
        month_digit = '01'
    if month[0] == 'февраля':
        month_digit = '02'
    if month[0] == 'марта':
        month_digit = '03'
    if month[0] == 'апреля':
        month_digit = '04'
    if month[0] == 'мая':
        month_digit = '05'
    if month[0] == 'июня':
        month_digit = '06'
    if month[0] == 'июля':
        month_digit = '07'
    if month[0] == 'августа':
        month_digit = '08'
    if month[0] == 'сентября':
        month_digit = '09'
    if month[0] == 'октября':
        month_digit = '10'
    if month[0] == 'ноября':
        month_digit = '11'
    if month[0] == 'декабря':
        month_digit = '12'

    return datetime.strptime(f"{year[0]}/{month_digit}/{day[0]}".strip(), '%Y/%m/%d')

